import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-recupera-contra',
  templateUrl: './recupera-contra.component.html',
  styleUrls: ['./recupera-contra.component.css']
})

export class RecuperaContraComponent implements OnInit {
  registroForm!: FormGroup;
  submitted = false;

  constructor(private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.registroForm = this.formBuilder.group({
      ruc: ['', Validators.required],
      docIdentidad: ['', Validators.required]
    });
  }

  get f() { return this.registroForm.controls; }

  onSubmit() {
    this.submitted = true;

    if (this.registroForm.invalid) {
      return;
    }

    alert('SUCCESS!! :-)\n\n' + JSON.stringify(this.registroForm.value, null, 4));
  }
}
